mapper.select:
select count(1) as totalRow from statistics_minute_http
where appid = #{appid}
 AND iid like CONCAT(CONCAT('%', #{condition}), '%')
 AND queryTime >= #{startTime}
 AND queryTime <= #{stopTime}

select a.* from statistics_minute_http a
where a.appid = #{appid}
 AND a.iid like CONCAT(CONCAT('%', #{condition}), '%')
 AND a.queryTime >= #{startTime}
 AND a.queryTime <= #{stopTime}
order by a.id
limit #{startId},#{pageLength}

select count(1) as totalRow from statistics_hour_http
where appid = #{appid}
AND queryTime >= #{startTime}
AND queryTime <= #{stopTime}

select a.* from statistics_hour_http a
where a.appid = #{appid}
AND queryTime >= #{startTime}
AND queryTime <= #{stopTime}
order by a.id
limit #{startId},#{pageLength}

