	select h.* ,a.appname,a.appdesc
	FROM statistics_minute_http h , appinfo a
	WHERE h.appid = #{appid} and h.appid = a.id
	and h.queryTime >= #{startTime} AND h.queryTime <= #{stopTime}

ORDER BY h.id desc
LIMIT 0,#{n}


	select h.* ,a.appname,a.appdesc
	FROM statistics_minute_http h , appinfo a
	WHERE h.id IN(
		select id FROM statistics_minute_http
		WHERE queryTime >= #{startTime} AND queryTime <= #{stopTime}
    ) and h.appid = a.id

ORDER BY h.hits
DESC
LIMIT 0,#{n}


	select h.* ,a.appname,a.appdesc
	FROM statistics_hour_http h , appinfo a
	WHERE h.id IN(
		select id FROM statistics_hour_http
		WHERE queryTime >= #{startTime} AND queryTime <= #{stopTime}
		GROUP BY appid 
    ) and h.appid = a.id

ORDER BY h.hits
DESC
LIMIT 0,#{n}


	select h.* ,a.appname,a.appdesc
	FROM statistics_day_http h , appinfo a
	WHERE h.id IN(
		select id FROM statistics_day_http
		WHERE queryTime >= #{startTime} AND queryTime <= #{stopTime}
		GROUP BY appid 
    ) and h.appid = a.id

ORDER BY h.hits
DESC
LIMIT 0,#{n}


	select h.* ,a.appname,a.appdesc
	FROM statistics_month_http h , appinfo a
	WHERE h.id IN(
		select id FROM statistics_month_http
		WHERE queryTime >= #{startTime} AND queryTime <= #{stopTime}
		GROUP BY appid 
    ) and h.appid = a.id

ORDER BY h.hits
DESC
